mapper.select:
select * from ( select rownum rn , ordergroup.* from ( select g.group_num groupNum , g.group_category_num groupCategoryNum , gc.group_category_name groupCategoryName , g.group_name as groupName , g.group_description as groupDescription , g.group_img_src as groupImgSrc , g.group_member_count as groupMemberCount , g.group_member_limit as groupMemberLimit , g.group_main_place as groupMainPlace , to_char(g.group_createdate, 'yyyy/mm/dd') as groupCreatedate from single_group g left outer join single_group_category gc on g.group_category_num = gc.group_category_num where g.group_statecode = '1'
and g.group_category_num = #{groupCategoryNum}
and g.group_member_count < g.group_member_limit and g.group_num not in ( select group_num from single_group_member gm where upper(gm.group_member_statecode) != upper('D') and gm.user_id = #{userId} )
and g.group_num in ( select group_num from single_group_member gm where upper(gm.group_member_statecode) != 'D' and upper(gm.group_member_statecode) != 'W' and gm.user_id = #{userId} )
order by g.group_num ) ordergroup where rownum <= #{endRow} ) rowgroup where rowgroup.rn > #{startRow}

mapper.insert:
insert into single_group(
    group_num
    , group_category_num
    , group_name
    , GROUP_DESCRIPTION
    , GROUP_IMG_SRC
    , GROUP_MEMBER_COUNT
    , GROUP_MEMBER_LIMIT
    , GROUP_MAIN_PLACE
    , GROUP_CREATEDATE
    , GROUP_STATECODE
)values(
    #{groupNum}
    , #{groupCategoryNum}
    , #{groupName}
    , #{groupDescription}
    , #{groupImgSrc}
    , #{groupMemberCount}
    , #{groupMemberLimit}
    , #{groupMainPlace}
    , sysdate
    , '1'
)

insert into single_calendar(
    calendar_num
    , group_num
    , calendar_subject
    , calendar_content
    , calendar_date
    , calendar_x_loc
    , calendar_y_loc
)values(
    #{calendarNum}
    , #{groupNum}
    , #{calendarSubject}
    , #{calendarContent}
    , to_date(#{calendarDate}, 'mm/dd/yyyy')
    , #{calendarXLoc}
    , #{calendarYLoc}
)

mapper.update:
update single_calendar
set
	calendar_subject = #{calendarSubject}
    , calendar_content = #{calendarContent}
    , calendar_date = to_date(#{calendarDate}, 'mm/dd/yyyy')
    , calendar_x_loc = #{calendarXLoc}
    , calendar_y_loc = #{calendarYLoc}
where
	calendar_num = #{calendarNum}

update single_group
set
	
    group_category_num = #{groupCategoryNum}
    , group_name = #{groupName}
    , group_description = #{groupDescription}
    , group_img_src = #{groupImgSrc}
    , group_member_limit = #{groupMemberLimit}
    , group_main_place = #{groupMainPlace}
    
    
    group_deletedate = sysdate
    , group_statecode = 0
    
where
    group_statecode = '1'
    and group_num = #{groupNum}

mapper.delete:
delete from single_hashtag
where
	hashtag_type_num = #{hashtagTypeNum}
	and group_num = #{groupNum}

mapper.select:
select count(*)
from single_stamp
where user_id = #{userId}
and group_num = #{groupNum}

mapper.update:
update single_group_member
set
	group_member_statecode = #{groupMemberStatecode}
	
	, group_joindate = sysdate
	, group_requestdate = null
	
where user_id = #{userId}

and group_num = #{groupNum}

mapper.delete:
delete from single_group_member
where
	group_num = #{groupNum}
	and user_id = #{userId}
